local veh_syncer = {}addEventHandler("onTrailerAttach", getRootElement(), function(theVehicle)	local driver = getVehicleOccupant(theVehicle)	if(driver) then		if(veh_syncer[source] ~= driver) then			veh_syncer[source] = driver 			setElementSyncer(source, driver)		end	endend)addEventHandler("onTrailerDetach", getRootElement(), function(theVehicle)	local x, y, z = getElementVelocity(theVehicle)	setElementVelocity(source, x, y, z)end)addEventHandler("onElementStopSync", getRootElement(), function(oldSyncer)	if(getElementType(source) == "vehicle") then		veh_syncer[source] = getElementSyncer(source)	endend)